<template>
  <transition :name="`${prefix}-fade`" :appear="true">
    <div
      :class="[
        `${prefix}`,
        type ? `${prefix}-${type}` : '',
        showClose ? `${prefix}-closable` : '',
        className
      ]"
      v-bind="attrs"
      v-show="visible"
      :style="positionStyle"
      @mouseenter="clearTimer"
      @mouseleave="startTimer">
      <i
        :class="[`${prefix}-icon`, typeIcon]"
        v-if="typeIcon" />
      <slot>
        <div v-if="!dangerouslyUseHTMLString" :class="`${prefix}-content`">
          {{ message }}
        </div>
        <div v-else v-html="message" :class="`${prefix}-content`" />
      </slot>
      <i
        v-if="showClose"
        :class="`${prefix}-close ${iconPrefixCls} ${iconPrefixCls}-close-thick`"
        @click="close" />
    </div>
  </transition>
</template>

<script>
import {
  getPrefix,
  getIconPrefix,
} from '@/utils/config';

const typeMap = {
  success: 'success-circle',
  info: 'info-circle',
  warning: 'warning-circle',
  error: 'error-circle',
  loading: 'loading',
};

export default {
  name: 'MtdMessage',

  data () {
    return {
      visible: true,
      message: '',
      duration: 3000,
      type: '',
      icon: '',
      className: '',
      offset: 0,
      onClose: null,
      showClose: false,
      closed: false,
      verticalProperty: 'top',
      verticalOffset: 0,
      timer: null,
      dangerouslyUseHTMLString: false,
      prefixCls: getPrefix(),
      iconPrefixCls: getIconPrefix(),
      attrs: {},
    };
  },

  computed: {
    prefix () {
      return `${this.prefixCls}-message`;
    },
    typeIcon () {
      const { iconPrefixCls } = this;
      return this.icon || (this.type && typeMap[this.type]
        ? `${iconPrefixCls}-${typeMap[this.type]}` : '');
    },

    positionStyle () {
      return {
        [this.verticalProperty]: `${this.verticalOffset}px`,
      };
    },
  },

  watch: {
    closed (newVal) {
      if (newVal) {
        this.visible = false;
        this.$el.addEventListener('transitionend', this.destroyElement);
      }
    },
  },

  created () {
    this.$on('esc', this.handleEsc);
  },

  mounted () {
    this.startTimer();
  },

  methods: {
    destroyElement () {
      this.$el.removeEventListener('transitionend', this.destroyElement);
      this.$destroy(true);
      this.$el.parentNode.removeChild(this.$el);
    },

    close () {
      this.closed = true;
      if (typeof this.onClose === 'function') {
        this.onClose();
      }
    },

    clearTimer () {
      clearTimeout(this.timer);
    },

    startTimer () {
      if (this.duration > 0) {
        this.timer = setTimeout(() => {
          if (!this.closed) {
            this.close();
          }
        }, this.duration);
      }
    },

    handleEsc (e) {
      if (!this.closed) {
        this.close();
      }
    },
  },
};
</script>
