<template>
  <div class="center-h">
    <mtd-button @click="openSuccMsg">成功</mtd-button>
    <mtd-button @click="openWarnMsg">警告</mtd-button>
    <mtd-button @click="openInfoMsg">消息</mtd-button>
    <mtd-button @click="openErrMsg">错误</mtd-button>
    <mtd-button @click="openLoading">加载</mtd-button>
  </div>
</template>
<script>
export default {
  methods: {
    openSuccMsg () {
      this.$mtd.message({
        message: '这是一条成功消息',
        type: 'success',
      });
    },

    openWarnMsg () {
      this.$mtd.message({
        message: '这是一条警告消息',
        type: 'warning',
      });
    },

    openInfoMsg () {
      this.$mtd.message({
        message: '这是一条常规消息',
        type: 'info',
      });
    },

    openErrMsg () {
      this.$mtd.message.error('这是一条错误消息');
    },

    openLoading () {
      this.$mtd.message({
        message: '这是一条正在加载的消息',
        type: 'loading',
      });
    },
  },
};
</script>
