<template>
<div>
  <transition name="fade-in">
    <div ref="mask" :class="`${prefix}-mask`"
      :style="{ 'z-index': zIndex }" v-show="visible"
      v-if="mask" @click="handleMaskClick" />
  </transition>
  <div v-show="wrapVisible"
    :style="{ 'z-index': zIndex }"
    :class="{
      [`${prefix}-wrapper`]: true,
      [`${prefix}-${placement}`]: placement,
      [`${prefix}-through`]: enableClickThrough,
    }"
    @click="handleWrapClick">
    <transition name="modal-fade"
      @after-enter="handleAfterEnter"
      @after-leave="handleAfterLeave">
      <modal v-bind="$attrs"
        :closable="closable"
        :title="title"
        :class="{
          [`${prefix}-fullscreen`]: fullscreen
        }"
        :style="{ 'width': modalWidth }"
        v-show="visible"
        v-if="modalVisible"
        @close="handleClose"
      >
        <slot name="title" v-if="$slots.title" slot="title" />
        <slot />
        <slot name="footer" v-if="$slots.footer" slot="footer" />
      </modal>
    </transition>
  </div>
</div>
</template>
<script>
import { hasClass } from '@/utils/dom';
import { lock, unlock } from '@/utils/lock-scroll';
import { PopupManage } from '@/utils/popper';
import { isNumber, isDef } from '@/utils/type';
import Modal from './inside';
import {
  getConfig,
  CONFIG_PROVIDER,
  getPrefixCls,
} from '@/utils/config';
import { hasProps } from '@/utils/vnode';

export default {
  name: 'MtdModal',
  components: {
    'modal': Modal,
  },
  inheritAttrs: true,
  model: {
    prop: 'visible',
  },
  props: {
    title: String,
    appendToContainer: {
      type: Boolean,
      default: true,
    },
    getPopupContainer: Function,
    mask: {
      type: Boolean,
      default: true,
    },
    closable: {
      type: Boolean,
      default: true,
    },
    maskClosable: {
      type: Boolean,
      default: false,
    },
    fullscreen: Boolean,
    visible: Boolean,
    destroyOnClose: Boolean,
    lockScroll: {
      type: Boolean,
      default: true,
    },
    placement: {
      type: String,
      validator (v) {
        return ['top', 'center'].indexOf(v) > -1;
      },
      default: 'center',
    },
    width: [String, Number],
    mountOnCreate: {
      type: Boolean,
      default: true,
    },
    enableClickThrough: {
      // to support: https://tt.sankuai.com/ticket/detail?id=40402170
      type: Boolean,
      default: false,
    },
    keyboard: Boolean,
  },
  inject: {
    config: {
      from: CONFIG_PROVIDER,
      default: {
        getPrefixCls,
      },
    },
  },
  data () {
    return {
      modalVisible: !this.mountOnCreate ? this.visible : true,
      wrapVisible: this.visible,
      zIndex: 2000,
    };
  },
  computed: {
    prefix () {
      return this.config.getPrefixCls('modal');
    },
    modalWidth () {
      return this.width
        ? (isNumber(this.width) ? `${this.width}px` : this.width)
        : undefined;
    },
    getContainer () {
      return this.getPopupContainer || this.config.getPContainer || getConfig().getPopupContainer;
    },
  },
  watch: {
    visible (n) {
      n ? this.openModal() : this.closeModal();
    },
  },
  created () {
    this.$on('esc', this.handleEscClose);
  },
  mounted () {
    this.init();
  },
  activated () {
    this.init();
  },
  deactivated () {
    this.destroy();
    this._modal = undefined;
  },
  beforeDestroy () {
    this.destroy();
  },
  methods: {
    init () {
      if (this.visible) {
        this.openModal();
      }
    },
    destroy () {
      this.closeModal(this);
      const parentNode = this.$el.parentNode;
      parentNode && parentNode.removeChild(this.$el);
    },
    createModal () {
      if (this.appendToContainer) {
        const getContainer = this.getContainer;
        const parent = getContainer();
        parent.appendChild(this.$el);
      }
      return true;
    },
    openModal () {
      this.modalVisible = true;
      this.wrapVisible = true;
      this.zIndex = PopupManage.nextZIndex();
      PopupManage.open(this);
      if (!this._modal) {
        this._modal = this.createModal();
      }
      if (this.lockScroll) {
        lock(document.body);
      }
      this.$emit('open');
    },
    closeModal () {
      PopupManage.close(this);
      if (this.lockScroll) {
        unlock(document.body);
      }
    },
    close () {
      this.$emit('close');
      this.$emit('input', false);
    },
    handleAfterLeave () {
      if (this.destroyOnClose) {
        this.modalVisible = false;
      }
      this.wrapVisible = false;
      this.$emit('closed');
    },
    handleAfterEnter () {
      this.$emit('opened');
    },
    handleMaskClick () {
      this.maskClosable && this.visible && this.close();
    },
    handleClose () {
      this.visible && this.close();
    },
    handleWrapClick (event) {
      if (hasClass(event.target, `${this.prefix}-wrapper`)) {
        this.handleMaskClick(event);
      }
    },
    handleEscClose (e) {
      // isDef: "keyboard" must be configured as a property in Confirm
      if ((hasProps(this, 'keyboard') && isDef(this.keyboard) ? this.keyboard : this.closable) && this.visible) {
        this.close();
      }
    },
  },
};
</script>
