<template>
  <div class="center-h">
    <mtd-button @click="openVNodeNotify">带操作按钮</mtd-button>
    <mtd-button @click="openHTMLNotify">带链接</mtd-button>
  </div>
</template>
<script>
export default {
  methods: {
    openVNodeNotify () {
      const h = this.$createElement;

      const n = this.$mtd.notify({
        title: '消息确认',
        type: 'info',
        duration: 0,
        showClose: false,
        message: h('div', {}, [
          h('div', '主要信息展示说明。'),
          h('mtd-button', {
            domProps: {
              innerHTML: '提交按钮',
            },
            style: {
              marginTop: '8px',
              float: 'right',
            },
            props: {
              type: 'primary',
              size: 'small',
            },
            on: {
              click () {
                n.close();
              },
            },
          }),
        ]),
      });
    },

    openHTMLNotify () {
      this.$mtd.notify({
        title: '失败信息',
        type: 'error',
        dangerouslyUseHTMLString: true,
        message: `2018.06.12 12:00:23 信息查询失败，立即跳转查看<a style="cursor: pointer; text-decoration: underline">失败原因</a>`,
      });
    },
  },
};
</script>
