<template>
  <div class="center-h">
    <mtd-button @click="openSuccNotify">成功</mtd-button>
    <mtd-button @click="openWarnNotify">警告</mtd-button>
    <mtd-button @click="openInfoNotify">消息</mtd-button>
    <mtd-button @click="openErrNotify">错误</mtd-button>
  </div>
</template>
<script>
export default {
  methods: {
    openSuccNotify () {
      this.$mtd.notify({
        title: '成功',
        message: `这是一条成功的提示消息。`,
        type: 'success',
      });
    },

    openWarnNotify () {
      this.$mtd.notify({
        title: '警告',
        message: '这是一条警告的提示消息。',
        type: 'warning',
      });
    },

    openInfoNotify () {
      this.$mtd.notify.info({
        title: '消息',
        message: `这是一条常规的提示消息。`,
      });
    },

    openErrNotify () {
      this.$mtd.notify.error({
        title: '错误',
        message: '这是一条错误的提示消息。',
      });
    },
  },
};
</script>
