import Picker from './control.js';
import { withFormItem } from '@components/form-item';

const MtdPicker = withFormItem('MtdPicker', Picker,
  {
    withRef: true,
    methods: {
      focus () {
        this.$refs.wrappedInstance.$refs.wrappedInstance.focus();
      },
      blur () {
        this.$refs.wrappedInstance.$refs.wrappedInstance.blur();
      },
    },
  },
);

/* istanbul ignore next */
MtdPicker.install = function (Vue) {
  Vue.component(MtdPicker.name, MtdPicker);
};

export default MtdPicker;
