<template>
  <div>
    <mtd-popconfirm placement="top"
      visible
      message="您确定要删除现有文档内容吗？"
      @ok="confirm"
      :icon="icon"
    >
      <mtd-button style="margin-right: 40px;">
        删除
      </mtd-button>
    </mtd-popconfirm>
  </div>
</template>
<script>
import { findVM } from '@test/util';

export default {
  data () {
    return {
      icon: 'mtdicon mtdicon-error-circle',
    };
  },
  methods: {
    confirm () {
      this.$mtd.message({
        type: 'success',
        message: '删除成功',
      });
    },
    async doTest (done) {
      const vm = findVM(this, { name: 'MtdPopperDrop' });
      expect(vm.$el.querySelector('.mtdicon-error-circle')).to.be.ok();
      done();
    },
  },
};
</script>
