import {
  mount
} from '@vue/test-utils';
import RadioButton from '../radio-button.vue';

describe('RadioButton', function () {
  it('input of radioButton', () => {
    const wrapper = mount(RadioButton)
    wrapper.trigger('click')
    expect(wrapper.emitted().input[0]).to.be.ok
  });
  it('checked of radioButton', () => {
    const wrapper = mount(RadioButton, {
      propsData: {
        checked: false
      }
    })
    wrapper.trigger('click')
    expect(wrapper.emitted().input[0][0]).to.be.equal(true)
  });
  it('disabled of radioButton', () => {
    const wrapper = mount(RadioButton, {
      propsData: {
        disabled: true
      }
    })
    expect(wrapper.classes()).to.include('mtd-radio-button-disabled')
    wrapper.trigger('click')
    expect(wrapper.emitted().input).to.not.be.ok
  });
  it('size of radioButton', () => {
    const wrapper = mount(RadioButton, {
      propsData: {
        size: 'small'
      }
    })
    expect(wrapper.classes()).to.include('mtd-radio-button-small')
  });
  it('name of radioButton', () => {
    const wrapper = mount(RadioButton, {
      propsData: {
        name: 'myRadio'
      }
    })
    const input = wrapper.find('input')
    expect(input.attributes().name).to.be.equal('myRadio')
  });
});
