<template>
  <div :class="prefix">
    <slot />
  </div>
</template>
<script>
import { CONFIG_PROVIDER,
  getPrefixCls,
  getSize,
} from '@/utils/config';

export default {
  name: 'RadioGroup',
  props: {
    value: [String, Number, Boolean, Function, Object, Array, Symbol],
    disabled: Boolean,
    size: String,
    name: String,
  },
  provide () {
    return {
      radioGroup: this,
    };
  },
  inject: {
    config: {
      from: CONFIG_PROVIDER,
      default: {
        getPrefixCls,
        getSize,
      },
    },
  },
  computed: {
    prefix () {
      return this.config.getPrefixCls('radio-group');
    },
    _size () {
      return this.config.getSize(this);
    },
  },
  created () {
    this.$on('radioChange', this.handleChange);
  },
  methods: {
    handleChange (radioValue) {
      if (this.value !== radioValue) {
        this.$emit('input', radioValue);
        this.$emit('change', radioValue);
      }
    },
  },
};
</script>
