<template>
  <radio-group v-bind="$attrs">
    <radio value="1" />
    <radio value="2" name="other" />
    <radio value="3" disabled />
    <radio value="4" checked />
    <radio value="5" size="large" />
  </radio-group>
</template>
<script>
import RadioGroup, { withRadioGroup } from '../../index.js';

const TestRadio = withRadioGroup('Radio', {
  name: 'DumbRadio',
  props: {
    value: [String, Number],
    disabled: Boolean,
    size: String,
    checked: Boolean,
    name: String,
  },
  methods: {
    handleClick (v) {
      this.$emit('input', true);
    },
  },
  render (h) {
    return h('div', {
      on: {
        click: this.handleClick,
      },
    });
  },
});

export default {
  name: 'SlotTest',
  components: {
    radio: TestRadio,
    RadioGroup,
  },
};
</script>
