import {
  mount
} from '@vue/test-utils';
import Radio from '../radio.vue';

describe('Radio', function () {
  it('input of radio', () => {
    const wrapper = mount(Radio)
    wrapper.trigger('click')
    expect(wrapper.emitted().input[0]).to.be.ok
  })
  it('checked of radio', () => {
    const wrapper = mount(Radio, {
      propsData: {
        checked: false
      }
    });
    wrapper.trigger('click')
    expect(wrapper.emitted().input[0][0]).to.be.equal(true)
  })
  it('disabled of radio', () => {
    const wrapper = mount(Radio, {
      propsData: {
        disabled: true
      }
    })
    expect(wrapper.classes()).to.include('mtd-radio-disabled')
    wrapper.trigger('click')
    expect(wrapper.emitted().input).to.not.be.ok
  })
  it('name of radio', () => {
    const wrapper = mount(Radio, {
      propsData: {
        name: 'myRadio'
      }
    })
    const input = wrapper.find('input')
    expect(input.attributes().name).to.be.equal('myRadio')
  })
});
