<template>
  <mtd-tooltip
    :content="tooltipContent"
    :placement="placement"
    :visible="tooltipVisible"
    ref="tooltip"
    @input="handleTooltipChange"
    size="small">
    <div
      :class="`${prefix}-button`"
      @mousedown="onMouseDown"
      @click.stop
      @touchstart="onMouseDown"
      :style="vertical
        ? {bottom: getPercent(value)}
        : {left: getPercent(value)}"
    />
  </mtd-tooltip>
</template>

<script>
import MtdTooltip from '@components/tooltip';
export default {
  name: 'MtdSliderButton',
  components: {
    MtdTooltip,
  },
  props: {
    prefix: {
      type: String,
      require: true,
    },
    value: {
      type: Number,
      default: 0,
    },
    vertical: {
      type: Boolean,
      default: false,
    },
    type: {
      type: [String, Number],
    },
    formatTooltip: Function,
    getPercent: Function,
  },
  data () {
    return {
      tip: false,
      dragging: false,
    };
  },
  computed: {
    tooltipContent () {
      return this.formatTooltip
        ? this.formatTooltip(this.value)
        : this.value.toString();
    },
    placement () {
      return this.vertical ? 'right' : 'top';
    },
    tooltipVisible () {
      return this.dragging ? true : this.tip;
    },
  },
  watch: {
    value () {
      this.$refs.tooltip.updatePopper();
    },
  },
  methods: {
    handleTooltipChange (v) {
      this.tip = v;
    },
    onMouseDown (e) {
      e.preventDefault();
      window.addEventListener('mousemove', this.onDragging);
      window.addEventListener('mouseup', this.onDragEnd);
    },
    onDragging (e) {
      if (!this.dragging) {
        this.startValue = this.value;
      }
      this.dragging = true;
      this.$emit('dragging', e, this.type);
    },
    onDragEnd (e) {
      this.dragging = false;
      this.$emit('drag-end', e, this.type);
      if (this.startValue !== this.value) {
        this.$emit('change');
      }
      window.removeEventListener('mousemove', this.onDragging);
      window.removeEventListener('mouseup', this.onDragEnd);
    },
  },
};
</script>
