<script>
import Icon from '@components/icon';

export default {
  name: 'MtdTabPage',

  props: {
    size: String,
    prevStatus: Boolean,
    nextStatus: Boolean,
    pageStyle: {
      type: Object,
      default () {
        return {
          right: 0,
        };
      },
    },
    prefix: {
      type: String,
      required: true,
    },
  },

  methods: {
    prevClick: function (event) {
      if (this.prevStatus) {
        this.$emit('prevClick', event);
      }
    },
    nextClick: function (event) {
      if (this.nextStatus) {
        this.$emit('nextClick', event);
      }
    },
  },

  render (h) {
    const {
      pageStyle,
      prevClick,
      nextClick,
      prevStatus,
      nextStatus,
      prefix,
    } = this;
    return (
      <span class={{
        [`${prefix}-page`]: true,
      }}
      style={pageStyle}>
        <Icon name="left-thick" class={{
          [`${prefix}-action`]: true,
          [`${prefix}-action-disabled`]: !prevStatus,
        }}
        onClick={prevClick}
        />
        <Icon name="right-thick"
          class={{
            [`${prefix}-action`]: true,
            [`${prefix}-action-disabled`]: !nextStatus,
          }}
          onClick={nextClick}
        />
      </span>
    );
  },
};
</script>
