// todo 目前直接引用 date-picker 组件的文件，导致单独 datepicker、timepicker 包中都会
// 包含这个文件
import TimePicker from '@components/date-picker/picker/time-picker.js';
import CreateComponent from '@components/date-picker/control';
import { withFormItem } from '@components/form-item';

const MtdTimePicker = withFormItem(
  'MtdTimePicker',
  CreateComponent('MtdTimePicker', TimePicker),
  {
    withRef: true,
  },
);

/* istanbul ignore next */
MtdTimePicker.install = function (Vue) {
  Vue.component(MtdTimePicker.name, MtdTimePicker);
};

export default MtdTimePicker;
