import {
  createDemoTest,
  mount,
  destroyVM,
  waitImmediate,
  triggerClick,
} from '@test/util';
import { TimePicker } from '@/index';

describe('TimePicker', function () {
  let vm;
  afterEach(() => {
    destroyVM(vm);
  })

  it('value string ', function () {
    const props = {
      value: '01:00',
      format: 'HH:mm'
    }
    vm = mount(TimePicker, { props: props, attrs: props });
    const input = vm.$el.querySelector('.mtd-input');
    expect(input.value).to.be.eql(props.value);
  });
});
