<template>
  <div class="demo-tree-filter-method">
    <mtd-input
      class="demo-filter"
      placeholder="输入关键字进行过滤"
      v-model="filterText"
    />

    <mtd-tree
      class="filter-tree"
      :data="data"
      empty-text="暂无数据"
      :filter-node-method="filterNode"
      default-expand-all
      filter-with-expand
      ref="tree" />
  </div>
</template>

<script>
export default {

  data () {
    return {
      filterText: '',
      data: [{
        id: 1,
        title: '一级 1',
        children: [{
          id: 4,
          title: '二级 1-1',
          children: [{
            id: 9,
            title: '三级 1-1-1',
          }, {
            id: 10,
            title: '三级 1-1-2',
          }],
        }],
      }, {
        id: 2,
        title: '一级 2',
        children: [{
          id: 5,
          title: '二级 2-1',
        }, {
          id: 6,
          title: '二级 2-2',
        }],
      }, {
        id: 3,
        title: '一级 3',
        children: [{
          id: 7,
          title: '二级 3-1',
        }, {
          id: 8,
          title: '二级 3-2',
        }],
      }],
    };
  },
  watch: {
    filterText (val) {
      this.$refs.tree.filter(val);
    },
  },

  methods: {
    filterNode (value, data) {
      if (!value) return true;
      return data.title.indexOf(value) !== -1;
    },
  },
};
</script>
<style lang="scss">
  .demo-tree .demo-tree-filter-method{
    text-align: left;
    width: 300px;
    .demo-filter{
      width: 100%;
      margin-bottom: 12px;
    }
    .mtd-tree{
      border: none;
    }
  }
</style>
