<template>
<div class="demo-filter-tree">
  <mtd-input v-model="query" class="demo-filter"
    placeholder="search" />
  <mtd-tree :data="data" node-key="title"
    :expanded-keys.sync="expandedKeys">
    <template slot-scope="scope">
      <text-highlight :queries="[query]">{{ scope.node.title }}</text-highlight>
    </template>
  </mtd-tree>
</div>
</template>
<script>
import TextHighlight from 'vue-text-highlight';
export default {
  components: {
    TextHighlight,
  },
  data () {
    return {
      query: '',
      expandedKeys: [],
      data: [{
        title: '唐诗',
        children: [{
          title: 'parent 1-1',
          children: [{
            title: 'leaf 1-1-1',
          }, {
            title: 'leaf 1-1-2',
          }],
        }, {
          title: 'parent 1-2',
          children: [{
            title: 'leaf 1-2-1',
          }],
        }],
      }, {
        title: '宋词',
        children: [{
          title: '苏轼 1-1',
          children: [{
            title: '东坡乐府 1-2',
          }, {
            title: '赤壁怀古 1-3',
          }],
        }],
      }, {
        title: '绝句',
      }],
    };
  },
  watch: {
    query (v) {
      if (v) {
        this.handleQuery();
      }
    },
  },
  methods: {
    filterNode (item) {
      return item.title.indexOf(this.query) > -1;
    },
    handleQuery () {
      const expandedKeys = [];
      const reduce = (state, data, parent) => {
        let result = false;
        data.forEach((item) => {
          if (item.children && reduce(state, item.children, item)) {
            result = true;
            state.push(item.title);
          } else if (this.filterNode(item)) {
            result = true;
          }
        });
        return result;
      };
      reduce(expandedKeys, this.data);
      this.expandedKeys = expandedKeys;
    },
  },
};
</script>
<style lang="scss">
  .demo-tree .demo-filter-tree{
    text-align: left;
    width: 300px;
    .demo-filter{
      width: 100%;
      margin-bottom: 12px;
    }
    .mtd-tree{
      border: none;
    }
  }
</style>
