<template>
  <div
    :class="[prefix, {
      [`${prefix}-expand-on-node`]: expandOnClickNode,
      [`${prefix}-draggable`]: draggable
    }]"
  >
    <mtd-tree-node
      v-for="child in root"
      :key="child.key"
      :node="child"
      :data="child.data"
    />
    <slot name="empty" v-if="!data || !data.length || root.allHidden">
      {{ emptyText }}
    </slot>
    <div
      v-show="dragState.showDropIndicator"
      :class="`${prefix}-drop-indicator`"
      ref="dropIndicator" />
  </div>
</template>
<script>
import MtdTreeNode from '@components/tree-node';
import {
  contains,
  removeChild,
  insertBefore,
  insertAfter,
  insertChild,
} from './util';
import { addClass, removeClass } from '@/utils/dom';
import { CONFIG_PROVIDER,
  getPrefixCls,
  getIconCls,
} from '@/utils/config';
import debounce from 'throttle-debounce/debounce';

function remove (key, keys) {
  const index = keys.indexOf(key);
  if (index > -1) {
    keys.splice(index, 1);
  }
  return index > -1;
}

function convertToMap (array) {
  return array.reduce((state, key) => {
    state[key] = true;
    return state;
  }, {});
}

const DEFAULT_FIELD_NAMES = {
  title: 'title',
  icon: 'icon',
  children: 'children',
  isLeaf: 'isLeaf',
  disabled: 'disabled',
  checkable: 'checkable',
  disableCheckbox: 'disableCheckbox',
};

export default {
  name: 'Tree',
  components: {
    MtdTreeNode,
  },
  inheritAttrs: false,
  props: {
    nodeClass: {
      type: [String, Function],
      default: '',
    },
    expandIcon: {
      type: String,
    },
    data: {
      type: Array,
      default () {
        return [];
      },
    },
    checkable: Boolean,
    checkedKeys: {
      type: Array,
      default () {
        return [];
      },
    },
    checkStrictly: {
      type: Boolean,
      default: false,
    },
    disabledStrictly: {
      type: Boolean,
      default: true,
    },
    checkedStrategy: {
      type: String,
      default: 'all',
      validator (v) {
        return ['all', 'parent', 'children'].indexOf(v) > -1;
      },
    },

    selectable: {
      type: Boolean,
      default: true,
    },
    selectedKeys: {
      type: Array,
      default () {
        return [];
      },
    },

    // defaultExpandAll: {
    //   type: Boolean,
    //   default: false,
    // },
    expandedKeys: {
      type: Array,
      default () {
        return [];
      },
    },
    autoExpandParent: {
      type: Boolean,
      default: false,
    },
    loadData: Function,
    loadedKeys: {
      type: Array,
      default () {
        return [];
      },
    },
    expandOnClickNode: {
      type: Boolean,
      default: true,
    },
    checkOnClickNode: {
      type: Boolean,
      default: false,
    },
    nodeKey: {
      type: String,
      default: 'id',
    },
    baseIndent: {
      type: Number,
      default: 0,
    },
    indent: {
      type: Number,
      default: 20,
    },
    participateDisabled: {
      type: Boolean,
      default: false,
    },
    emptyText: String,

    props: {
      type: Object,
      default () {
        return {};
      },
    },

    draggable: Boolean,
    allowDrag: Function,
    allowDrop: Function,
    filterNodeMethod: Function,
    filterWithExpand: Boolean, // 过滤是否伴随更新expandKeys的动作
  },
  provide () {
    return {
      tree: this,
    };
  },
  inject: {
    config: {
      from: CONFIG_PROVIDER,
      default: {
        getPrefixCls,
        getIconCls,
      },
    },
  },
  data () {
    return {
      forceNumber: 1,
      dragState: {
        showDropIndicator: false,
        draggingNode: undefined,
        dropNode: null,
        allowDrop: true,
      },
      state: {
        expandedKeys: [],
      },
      query: '',
    };
  },
  computed: {
    prefix () {
      return this.config.getPrefixCls('tree');
    },
    _expandIcon () {
      return this.expandIcon || this.config.getIconCls('triangle');
    },
    fieldNames () {
      return {
        ...DEFAULT_FIELD_NAMES,
        ...this.props,
      };
    },
    checkedKeyMap () {
      if (!this.checkable) {
        return {};
      }
      return convertToMap(this.checkedKeys);
    },
    loadedKeyMap () {
      return convertToMap(this.loadedKeys);
    },
    selectedKeyMap () {
      return convertToMap(this.selectedKeys);
    },
    root () {
      const flatNodes = {};
      const root = this.createNodes(this.data, flatNodes);
      this.buildNodes(flatNodes);
      if (this.forceNumber) {
        // 仅仅是让 root 重新计算
        root.forceNumber = this.forceNumber;
      }
      this.flatNodes = flatNodes; // eslint-disable-line
      return root;
    },
  },
  watch: {
    data (n, v) {
      if (n !== v) {
        this.state.expandedKeys = [];
        this.filterNodeMethod && this.filter('');
      }
    },
  },
  created () {
    this.$on('expandedChange', this.handleExpanded);
    this.$on('checkedChange', this.handleChecked);
    this.$on('loadedData', this.handleDataLoaded);
    this.$on('selectedNode', this.handleSelectedNoded);
    this.$on('nodeClick', this.handleNodeClick);
    this.filter = debounce(500, this.filter);
  },
  methods: {
    // public methods
    filter: debounce(300, function (val) {
      const tempExpandedKeys = [];
      const { filterNodeMethod } = this;
      if (!filterNodeMethod) {
        throw new Error('[MTD Tree] filterNodeMethod is required when filter');
      }
      const traverse = (nodes) => {
        if (nodes) {
          nodes.forEach((node) => {
            let childVisible = false;
            if (node.children) {
              childVisible = traverse(node.children);
            }
            node.visible = childVisible || filterNodeMethod(val, node.data, node);
            if (node.visible && node.children) {
              const { key } = node;
              tempExpandedKeys.push(key);
            }
          });
          return nodes.some((node) => node.visible);
        }
        return false;
      };
      this.root.allHidden = !traverse(this.root);
      if (this.filterWithExpand && val) {
        this.$emit('update:expandedKeys', tempExpandedKeys);
      }
      this.query = val;
      this.$forceUpdate();
    }),

    // private
    handleDataLoaded (node, data, children) {
      const { key } = node;
      this.$set(data, 'children', children);
      this.$emit('update:loadedKeys', [...this.loadedKeys, key]);
      this.filterNodeMethod && this.filter(this.query);
    },

    handleExpanded (node, expanded) {
      const { key } = node;
      const next = [...this.state.expandedKeys];
      if (expanded) {
        next.push(key);
      } else {
        remove(key, next);
      }
      const nodes = this.getNodesByKeys(next);
      this.$emit('update:expandedKeys', next);
      this.$emit('toggle-expand', next, {
        node,
        nodes,
        data: node.data,
        expanded,
      });
    },

    getNodesByKeys (keys) {
      return keys.map((key) => {
        return this.flatNodes[key];
      });
    },

    getNode (key) {
      return this.flatNodes[key];
    },

    handleChecked (node, checked) {
      const { key } = node;
      let keys = [...this.checkedKeys];
      let nodes;
      if (this.checkStrictly) {
        if (checked) {
          keys.push(key);
        } else {
          const index = keys.indexOf(key);
          if (index > -1) {
            keys.splice(index, 1);
          }
        }
      } else {
        if (checked) {
          node.checked = true;
          this.updateParentChecked(node);
          this.updateChildChecked(node);
        } else {
          node.checked = false;
          this.updateParentUnChecked(node);
          this.updateChildUnChecked(node);
        }
        nodes = this.getCheckedNodes(this.root);
        keys = nodes.map((n) => n.key);
      }
      this.forceNumber++;
      if (!nodes) {
        nodes = this.getNodesByKeys(keys);
      }
      this.$emit('update:checkedKeys', keys);
      this.$emit('toggle-checked', keys, {
        node,
        nodes,
        checked,
      });
    },

    getCheckedNodes (nodes, checkedStrategy = this.checkedStrategy) {
      const checkedNodes = [];
      function flatChecked (node) {
        const { children, checked, checkable } = node;
        if (checked && checkable) {
          checkedNodes.push(node);
        }
        children && children.forEach(flatChecked);
      }
      nodes.forEach(flatChecked);
      if (checkedStrategy === 'children') {
        return checkedNodes.filter((node) => {
          return !node.children;
        });
      } else if (checkedStrategy === 'parent') {
        return checkedNodes.filter((node) => {
          const { $parent } = node;
          return !$parent || !$parent.checkable || !$parent.checked;
        });
      };
      return checkedNodes;
    },

    getHalfCheckedNodes () {
      const nodes = this.root;
      const halfCheckedNodes = [];
      function flatHalfChecked (node) {
        const { children, checked, checkable, indeterminate } = node;
        if (indeterminate) {
          halfCheckedNodes.push(node);
        }
        if (checkable && (checked || indeterminate)) {
          children && children.forEach(flatHalfChecked);
        }
      }
      nodes.forEach(flatHalfChecked);
      return halfCheckedNodes;
    },

    createNodes (data, flats, parentNode, level = 0) {
      const {
        disabled: disabledField,
        checkable: checkableField,
        disableCheckbox: disableCheckboxField,
        children: childField,
        isLeaf: isLeafField,
        title: titleField,
        icon: iconField,
      } = this.fieldNames;

      return data.map((item) => {
        const key = item[this.nodeKey];
        const oldNode = this.flatNodes && this.flatNodes[key];

        const node = {
          key: key,
          $parent: parentNode,
          data: item,
          level: level,
          disabled: (disabledField in item) ? item[disabledField]
            : (!this.disabledStrictly ? false : (parentNode && parentNode.disabled)),
          checkable: (checkableField in item) ? item[checkableField] : this.checkable,
          isLeaf: item[isLeafField],
          title: item[titleField],
          icon: item[iconField],
          visible: oldNode ? oldNode.visible : true, // 上一次节点的visible状态缓存
        };
        Object.assign(node, {
          selected: this.selectedKeyMap[node.key],
          loaded: this.loadedKeyMap[node.key],
          disableCheckbox: item[disableCheckboxField] || node.disabled,
          children: item[childField] && item[childField].length
            ? this.createNodes(item[childField], flats, node, level + 1)
            : undefined,
        });
        flats[node.key] = node;
        return node;
      });
    },

    buildNodes (flatNodes) {
      const expandedNodes = this.expandedKeys.map((key) => {
        return flatNodes[key];
      }).filter((node) => !!node);

      const expandedKeys = [...this.expandedKeys];

      expandedNodes.forEach((node) => {
        node.expanded = true;
        let parent = node.$parent;
        if (!this.state.expandedKeys.length && this.autoExpandParent) {
          while (parent && !parent.expanded) {
            // 首次生效
            parent.expanded = true;
            expandedKeys.push(parent.key);
            parent = parent.$parent;
          }
        }
      });
      this.state.expandedKeys = this.removeDuplicationFromArr(expandedKeys);
      // this.state.expandedKeys = expandedKeys;
      if (expandedKeys.length !== this.expandedKeys.length) {
        this.$emit('update:expandedKeys', this.state.expandedKeys);
      }

      const checkedNodes = this.checkedKeys.map((key) => {
        return flatNodes[key];
      }).filter((node) => !!node);
      checkedNodes.forEach((node) => {
        node.checked = true;
      });
      if (!this.checkStrictly) {
        checkedNodes.forEach(this.updateChildChecked);
        checkedNodes.forEach(this.updateParentChecked);
      }
    },

    updateParentChecked (node) {
      const { $parent: parent } = node;
      // participateDisabled即便disabled父节点无法主动改变状态，但是子节点的改变是可以影响的
      if (this.participateDisabled ? parent : (parent && !parent.disableCheckbox)) {
        const { children } = parent;
        const allChecked = children.every((child) => {
          return child.checked || (!this.participateDisabled && child.disableCheckbox);
        });

        parent.checked = allChecked;
        parent.indeterminate = !allChecked;
        this.updateParentChecked(parent);
      }
    },

    updateChildChecked (node) {
      const { children } = node;
      if (this.participateDisabled ? (children && !node.disabled) : children) {
        children.forEach((item) => {
          if (!item.checked && !item.disableCheckbox) {
            item.checked = true;
            this.updateChildChecked(item);
          }
        });
      }
    },

    updateParentUnChecked (node) {
      const { $parent: parent } = node;
      if (parent) {
        const { children } = parent;
        const anyChecked = children.some((child) => {
          // 忽略 disabledCheckbox 的选项
          return !child.disableCheckbox &&
            (child.checked || child.indeterminate);
        });

        parent.checked = false;
        parent.indeterminate = anyChecked;
        this.updateParentUnChecked(parent);
      }
    },

    updateChildUnChecked (node) {
      const { children } = node;
      if (children) {
        children.forEach((item) => {
          if (!item.disableCheckbox) {
            item.checked = false;
            this.updateChildUnChecked(item);
          }
        });
      }
    },

    handleSelectedNoded (node, selected) {
      const next = selected ? [node.key] : [];
      this.$emit('update:selectedKeys', next);
      this.$emit('toggle-selected', node, selected);
    },

    handleNodeClick (...args) {
      this.$emit('node-click', ...args);
    },
    // Thanks to https://github.com/ElemeFE/element/blob/dev/packages/tree/src/tree.vue
    handleDragStart (event, treeNode) {
      if (typeof this.allowDrag === 'function' && !this.allowDrag(treeNode.node)) {
        event.preventDefault();
        return false;
      }
      event.dataTransfer.effectAllowed = 'move';
      // wrap in try catch to address IE's error when first param is 'text/plain'
      try {
        // setData is required for draggable to work in FireFox
        // the content has to be '' so dragging a node out of the tree won't open a new tab in FireFox
        event.dataTransfer.setData('text/plain', '');
      } catch (e) {}
      this.dragState.draggingNode = treeNode;
      this.$emit('node-drag-start', treeNode.node);
    },
    handleDragOver (event, treeNode) {
      const dropNode = treeNode;
      const { dragState } = this;

      const oldDropNode = dragState.dropNode;
      if (oldDropNode && oldDropNode !== dropNode) {
        removeClass(oldDropNode.$el, `${this.prefix}-drop-inner`);
      }
      const draggingNode = dragState.draggingNode;
      if (!draggingNode || !dropNode) {
        return;
      }

      // 判断插入位置
      let dropPrev = true;
      let dropInner = true;
      let dropNext = true;
      let userAllowDropInner = true;
      if (typeof this.allowDrop === 'function') {
        dropPrev = this.allowDrop(draggingNode.node, dropNode.node, 'prev');
        userAllowDropInner = dropInner = this.allowDrop(draggingNode.node, dropNode.node, 'inner');
        dropNext = this.allowDrop(draggingNode.node, dropNode.node, 'next');
      }
      event.dataTransfer.dropEffect = dropInner ? 'move' : 'none';
      if ((dropPrev || dropInner || dropNext) && oldDropNode !== dropNode) {
        if (oldDropNode) {
          this.$emit('node-drag-leave', draggingNode.node, oldDropNode.node);
        }
        this.$emit('node-drag-enter', draggingNode.node, dropNode.node);
      }
      if (dropPrev || dropInner || dropNext) {
        dragState.dropNode = dropNode;
      }

      if (dropNode.node.nextNode === draggingNode.node) {
        // 已经是下一级，不能插入在下一个
        dropNext = false;
      }
      if (dropNode.node.previousNode === draggingNode.node) {
        // 已经是上一级，不能插入在前一个
        dropPrev = false;
      }
      if (contains(dropNode.node, draggingNode.node, false)) {
        // 已经是子集，不能插入在内
        dropInner = false;
      }
      if (draggingNode.node === dropNode.node ||
        contains(draggingNode.node, dropNode.node)) {
        // 原位置 或是 移动至自身的子集
        dropPrev = false;
        dropInner = false;
        dropNext = false;
      }
      const targetPosition = dropNode.$el.querySelector(`.${this.prefix}-node-content`)
        .getBoundingClientRect();
      const treePosition = this.$el.getBoundingClientRect();

      let dropType;
      const prevPercent = dropPrev ? (dropInner ? 0.25 : (dropNext ? 0.45 : 1)) : -1;
      const nextPercent = dropNext ? (dropInner ? 0.75 : (dropPrev ? 0.55 : 0)) : 1;

      const distance = event.clientY - targetPosition.top;
      if (distance < targetPosition.height * prevPercent) {
        dropType = 'before';
      } else if (distance > targetPosition.height * nextPercent) {
        dropType = 'after';
      } else if (dropInner) {
        dropType = 'inner';
      } else {
        dropType = 'none';
      }

      let indicatorTop = -9999;
      const iconPosition = dropNode.$el
        .querySelector(`.${this.prefix}-node-expand-icon`)
        .getBoundingClientRect();

      const contentPosition = dropNode.$el
        .querySelector(`.${this.prefix}-node-content-wrapper`)
        .getBoundingClientRect();

      const dropIndicator = this.$refs.dropIndicator;
      if (dropType === 'before') {
        indicatorTop = contentPosition.top - treePosition.top - 2; // 样式问题，顶部增加 2px 位移
      } else if (dropType === 'after') {
        indicatorTop = contentPosition.bottom - treePosition.top;
      }
      dropIndicator.style.top = indicatorTop + 'px';
      dropIndicator.style.left = (iconPosition.right - treePosition.left) + 'px';

      if (dropType === 'inner') {
        addClass(dropNode.$el, `${this.prefix}-drop-inner`);
      } else {
        removeClass(dropNode.$el, `${this.prefix}-drop-inner`);
      }
      dragState.showDropIndicator = dropType === 'before' || dropType === 'after';
      dragState.allowDrop = dragState.showDropIndicator || userAllowDropInner;
      dragState.dropType = dropType;
      this.$emit('node-drag-over', draggingNode.node, dropNode.node);
    },
    handleDragEnd (event, treeNode) {
      const { dragState } = this;
      const { draggingNode, dropType, dropNode } = dragState;
      event.preventDefault();
      event.dataTransfer.dropEffect = 'move';

      // 将 node 改为 data
      if (draggingNode && dropNode) {
        const draggingNodeCopy = draggingNode.node;
        const draggingParent = draggingNode.node.$parent
          ? draggingNode.node.$parent
          : { data: { [this.fieldNames.children]: this.data }, children: this.root };
        const drogParent = dropNode.node.$parent
          ? dropNode.node.$parent
          : { data: { [this.fieldNames.children]: this.data }, children: this.root };

        if (dropType !== 'none') {
          removeChild(
            draggingParent,
            draggingNode.node,
            this.fieldNames,
          );
        }
        if (dropType === 'before') {
          insertBefore(
            drogParent,
            draggingNodeCopy,
            dropNode.node,
            this.fieldNames,
          );
        } else if (dropType === 'after') {
          insertAfter(
            drogParent,
            draggingNodeCopy,
            dropNode.node,
            this.fieldNames,
          );
        } else if (dropType === 'inner') {
          insertChild(
            dropNode.node,
            draggingNodeCopy,
            -1,
            this.fieldNames,
          );
        }

        removeClass(dropNode.$el, `${this.prefix}-drop-inner`);

        this.$emit('node-drag-end', draggingNode.node, dropNode.node, dropType);
        if (dropType !== 'none') {
          this.$emit('node-drop', draggingNode.node, dropNode.node, dropType);
        }
      }
      if (draggingNode && !dropNode) {
        this.$emit('node-drag-end', draggingNode.node, null, dropType);
      }
      dragState.showDropIndicator = false;
      dragState.draggingNode = null;
      dragState.dropNode = null;
      dragState.allowDrop = true;
    },

    // 数组去重
    removeDuplicationFromArr (arr) {
      return Array.from(new Set(arr));
    },
  },
};
</script>
