import Vue from 'vue';
import Main from './component';
import { isVNode } from '@ss/mtd-vue/es/utils/vnode';
import { PopupManage } from '@ss/mtd-vue/es/utils/popper';
import { getConfig } from '@ss/mtd-vue/es/utils/config';

var MessageConstructor = Vue.extend(Main);

var instances = [];
var seed = 1;
// message的间隔距离px
var spacing = 12;

function updateOffset() {
  var position = spacing;
  instances.forEach(function (instance) {
    instance.verticalOffset = position + instance.offset;
    position = position + instance.$el.offsetHeight + spacing;
  });
}

var Message = function Message(options) {
  options = options || {};
  if (typeof options === 'string') {
    options = {
      message: options
    };
  }

  var userOnClose = options.onClose;
  var id = 'message_' + seed++;

  options.onClose = function () {
    Message.close(id, userOnClose);
  };
  var instance = new MessageConstructor({
    data: options
  });
  if (isVNode(instance.message)) {
    instance.$slots.default = [instance.message];
    instance.message = null;
  }
  instance.id = id;
  instance.$mount();

  var _options = options,
      getPopupContainer = _options.getPopupContainer;

  var parent = getPopupContainer ? getPopupContainer() : getConfig().getPopupContainer();
  parent.appendChild(instance.$el);

  instance.dom = instance.$el;
  instance.dom.style.zIndex = PopupManage.nextZIndex();
  PopupManage.open(instance);

  var verticalOffset = options.offset || 0;
  instances.forEach(function (item) {
    verticalOffset += item.$el.offsetHeight + spacing;
  });
  verticalOffset += spacing;
  instance.verticalOffset = verticalOffset;
  instances.push(instance);
  return instance;
};

['success', 'warning', 'info', 'error', 'loading'].forEach(function (type) {
  Message[type] = function (options) {
    if (typeof options === 'string' || isVNode(options)) {
      options = {
        message: options
      };
    }
    options.type = type;
    return Message(options);
  };
});

Message.close = function (id, userOnClose) {
  var index = -1;
  var len = instances.length;
  var instance = instances.filter(function (instance, i) {
    if (instance.id === id) {
      index = i;
      return true;
    }
    return false;
  })[0];
  if (!instance) return;
  PopupManage.close(instance);

  if (typeof userOnClose === 'function') {
    userOnClose(instance);
  }
  instances.splice(index, 1);

  if (len <= 1) return;
  var removedHeight = instance.dom.offsetHeight;
  for (var i = index; i < len - 1; i++) {
    instances[i].dom.style[instance.verticalProperty] = parseInt(instances[i].dom.style[instance.verticalProperty], 10) - removedHeight - spacing + 'px';
  }
};

Message.closeAll = function () {
  for (var i = instances.length - 1; i >= 0; i--) {
    instances[i].close();
  }
  instances.splice(0, instances.length);
};

Message.updateOffset = updateOffset;
Message.COMPONENT = Main;

export default Message;