import Textarea from './textarea';
import { withFormItem } from '@ss/mtd-vue/es/components/form-item';

var MtdTextarea = withFormItem('MtdTextarea', Textarea, {
  withRef: true,
  methods: {
    focus: function focus() {
      return this.$refs.wrappedInstance.focus();
    },
    blur: function blur() {
      return this.$refs.wrappedInstance.blur();
    },
    select: function select() {
      return this.$refs.wrappedInstance.select();
    }
  }
});

/* istanbul ignore next */
MtdTextarea.install = function (Vue) {
  Vue.component(MtdTextarea.name, MtdTextarea);
};

export default MtdTextarea;