// todo 目前直接引用 date-picker 组件的文件，导致单独 datepicker、timepicker 包中都会
// 包含这个文件
import TimePicker from '@ss/mtd-vue/es/components/date-picker/picker/time-picker.js';
import CreateComponent from '@ss/mtd-vue/es/components/date-picker/control';
import { withFormItem } from '@ss/mtd-vue/es/components/form-item';

var MtdTimePicker = withFormItem('MtdTimePicker', CreateComponent('MtdTimePicker', TimePicker), {
  withRef: true
});

/* istanbul ignore next */
MtdTimePicker.install = function (Vue) {
  Vue.component(MtdTimePicker.name, MtdTimePicker);
};

export default MtdTimePicker;