import { mergeDeep } from '../util';

describe('Utils', function () {
  describe('mergeDeep', function () {
    it('base', function () {
      var a = {
        a: 1,
        b: [1, 2],
        c: {
          d: 3
        }
      };
      var b = {
        a: 2,
        c: {
          e: 4
        },
        f: 5
      };
      var c = mergeDeep(a, b);
      expect(c).to.be.equal(a);
      expect(c.c).to.be.not.equal(b.c);
      expect(c).to.be.eql({
        a: 2,
        b: [1, 2],
        c: {
          d: 3,
          e: 4
        },
        f: 5
      });
    });
  });
});