//算法1：lxcuid生成算法
exports.UUID = function () {
  const s = [];
  const hexDigits = "0123456789abcdef";
  for (let i = 0; i < 36; i++) {
    s[i] = hexDigits.substr(Math.floor(Math.random() * 0x10), 1);
  }
  s[14] = "4";
  s[19] = hexDigits.substr((s[19] & 0x3) | 0x8, 1);
  // s[8] = s[13] = s[18] = s[23] = "1";

  const uuid = s.join("");
  return uuid;
};

//算法2：灵犀websdk msid生成算法
exports.rndSeed = function () {
  let seed = Math.floor(1 + Math.random() * 65535);
  return seed.toString(16).substring(1);
}
/**
* msid 生成算法
* @returns {string} msid 28位随机串
* @private
*/
exports.MSID = function () {
  let seeds = [];
  let time = +new Date;
  seeds.push(time.toString(16));
  seeds.push(rndSeed());
  seeds.push(rndSeed());
  seeds.push(rndSeed());
  return seeds.join('-');
}

//算法3：灵犀websdk req_id生成算法
exports.genReqId = function () {
  return (+new Date).toString(16) + '-' + Math.floor(Math.random() * 65535) + '-' + Math.floor(Math.random() * 65535);
}
