const chalk = require('chalk');
const file = require('fs');
const path = require('path');

function resolve (...dirs) {
  return path.join(__dirname, '..', ...dirs);
}

const tokenFile = resolve('token.json');
module.exports = function getToken (name) {
  if (!file.existsSync(tokenFile)) {
    console.log(chalk.red('please create token.json in root dir'));
    throw new Error('unable got token.json');
  }
  const token = require(tokenFile);
  return token[name];
}
