<template>
  <todo :todos="todos">
    <p class="todo-slot-template" slot-scope="{ todo }">{{ todo.name }}</p>
  </todo>
</template>
<script>
import createHoc from '@/utils/hoc';
import todo from './todos';

export const length = 5;

export default {
  components: {
    todo: createHoc()(todo),
  },
  data () {
    const todos = [];
    for (let i = 0; i < length; i++) {
      todos.push({
        id: i,
        text: `text-todo-item-${i}`,
        name: `name-todo-item-${i}`,
      });
    }
    return {
      todos: todos,
    };
  },
};
</script>
