<template>
    <div>
        <MpaConditionExpression
            ref="expression"
            filterTextWidth="40px"
            :conditionData="conditionData"
            :hasRootCondition="false"
            :filterText="null"
            :addChildButtonLevel="3"
            :filterHeighLight="false"
            @addCondition="addCondition"
        >
            <template v-slot:addGroups>
                <mtd-button type="text" icon="mtdicon mtdicon-file-add-o" @click="addGroup">添加后续行为</mtd-button>
            </template>
        </MpaConditionExpression>
    </div>
</template>
<style></style>
<script lang="ts">
import { Vue, Component } from 'vue-property-decorator';
import MpaConditionExpression from '../../mpa-condition-expression.vue';
import BehaviorCase from '../../test/behavior-case.vue';
import TestCondition from '../../test/test-condition.vue';
Vue.component(TestCondition.name, TestCondition);
Vue.component(BehaviorCase.name, BehaviorCase);
@Component({
    components: {
        MpaConditionExpression,
    },
})
export default class Base extends Vue {
    private conditionData = {
        name: '',
        conditionType: 'and',
        childConditionList: [
            {
                comp: 'behavior-case',
                operatorList: ['filters'],
                data: {
                    title: '初始行为',
                    value: 'view',
                },
            },
            {
                comp: 'behavior-case',
                operatorList: ['filters', 'copy', 'delete'],
                conditionType: 'or',
                data: {
                    title: '后续行为',
                    value: 'app',
                },
                childConditionList: [
                    {
                        comp: 'test-condition',
                        data: {
                            key: '01',
                            operator: 'in',
                            value: '马',
                        },
                    },
                    {
                        comp: 'test-condition',
                        data: {
                            key: '02',
                            operator: 'in',
                            value: '敏感度低',
                        },
                    },
                ],
            },
        ],
    };

    private addCondition({ name, comp, callback }) {
        callback({
            comp: 'test-condition',
            data: {
                key: '01',
                operator: 'in',
                value: '马',
            },
        });
    }
    private validate() {
        (this.$refs.expression as MpaConditionExpression).validate();
    }

    private addGroup() {
        (this.$refs.expression as MpaConditionExpression).addRootCondition({
            comp: 'behavior-case',
            operatorList: ['filters', 'copy', 'delete'],
            conditionType: 'or',
            data: {
                title: '后续行为',
                value: 'app',
            },
        });
    }
}
</script>
<style lang="scss" scoped>
.mtd-btn-text {
    color: #166FF7;
    font-size: 12px;
    padding-left: 0px;
}
</style>
