<template>
    <div class="behavior-container">
        <span class="chart-index">
            {{characterCode}}
        </span>
        <span class="title">{{ value.title }}</span>
        <mtd-select v-model="value.value" :disabled="disabled" style="width: 200px">
            <mtd-option
                v-for="item in options"
                :key="item.value"
                :label="item.label"
                :value="item.value"
                :disabled="item.disabled"
            />
        </mtd-select>
    </div>
</template>
<script lang="ts">
import { Vue, Component, Prop } from 'vue-property-decorator';
import { generateBigCharacter } from '../utils/index';
@Component
export default class BehaviorCase extends Vue {
    @Prop({
        type: Object,
        default: () => {
            return {
                title: '初始行为',
                value: 'view',
            };
        },
    })
    private value;

    @Prop({
        type: Number,
        default: 0
    })
    private index: number;

    @Prop({ type: Boolean, default: false })
    private disabled;

    private get characterCode() {
        return generateBigCharacter(this.index);
    }

    private get options() {
        return [
            {
                value: 'view',
                label: '浏览页面',
            },
            {
                value: 'app',
                label: 'APP激活',
            },
        ];
    }
}
</script>
