<template>
  <div :class="classes">
    <span
      :class="getCellCls(cell)"
      v-for="(cell, i) in cells"
      :key="i"
      @click="handleClick(cell)"
    ><em>{{ cell.text }}</em></span>
  </div>
</template>
<script>
import dayjs, { clearHours } from './../../../utils/date';
import { deepCopy } from './../../../utils/utils';
import mixin from './mixin';
import Locale from './../../../mixins/locale';
import {
  CONFIG_PROVIDER,
  getPrefixCls,
} from './../../../utils/config';

export default {
  mixins: [ mixin, Locale ],

  props: {/* in mixin */},
  inject: {
    config: {
      from: CONFIG_PROVIDER,
      default: {
        getPrefixCls,
      },
    },
  },
  computed: {
    prefix() {
      return this.config.getPrefixCls('date-picker');
    },
    prefixCls() {
      return `${this.prefix}-cells`;
    },
    classes() {
      const { prefixCls } = this;
      return [
        `${prefixCls}`,
        `mpa-date-picker-cells-halfyear`,
      ];
    },
    year() {
      return this.tableDate.getFullYear();
    },
    cells() {
      const cells = [];
      const cellTmpl = {
        text: '',
        selected: false,
        disabled: false,
      };

      const selectedHalfYears = this.dates.filter(Boolean).filter((date) => {
        return date.getFullYear() === this.year;
      }).map((date) => {
        return this.getHalfYear(date);
      });
      const focusedDate = clearHours(
        new Date(this.focusedDate.getFullYear(), 0, 1),
      );
      const disabledTestFn = typeof this.disabledDate === 'function' &&
        this.disabledDate;
      for (let i = 1; i < 3; i++) {
        const cell = deepCopy(cellTmpl);
        cell.date = new Date(this.year, (i - 1) * 6, 1);
        cell.disabled = (cell.date && disabledTestFn) &&
            disabledTestFn(cell.date);
        const day = clearHours(cell.date);
        cell.selected = selectedHalfYears.indexOf(i) > -1;
        cell.focused = day === focusedDate;
        cell.range = this.pickerType === 'halfyearrange'
          && (new Date(day) > this.dates[0] && new Date(day) < this.dates[1]);
        cell.text = `${this.t(`el.datepicker.halfyear${i}`)}`;
        cells.push(cell);
      }
      return cells;
    },
  },
  methods: {
    getHalfYear(date) {
      const halfFormatted = dayjs(date).quarter() < 3 ? 1 : 2;
      return halfFormatted;
    },
    getCellCls(cell) {
      const { prefixCls } = this;
      return [
        `${prefixCls}-cell`,
        {
          [`${prefixCls}-cell-selected`]: cell.selected,
          [`${prefixCls}-cell-disabled`]: cell.disabled,
          [`${prefixCls}-cell-focused`]: cell.focused,
          [`${prefixCls}-cell-range`]: cell.range && !cell.start && !cell.end,
        },
      ];
    },
  },
};
</script>
<style lang="scss"></style>
