<template>
  <div>
    <mtd-row>
      <mtd-col :span="12">
        <p>无侧边面板</p>
        <mpa-date-picker
          type="date"
          v-model="value"
          placeholder="选择时间"/>
      </mtd-col>
      <mtd-col :span="12">
        <p>有侧边面板</p>
        <mpa-date-picker
          type="date"
          v-model="value"
          placeholder="选择时间"
          :options="options" />
      </mtd-col>
    </mtd-row>
  </div>
</template>
<script lang="ts">
import { Vue, Component } from 'vue-property-decorator';
const shortcuts = [{
  text: '今天',
  value() {
    return new Date();
  },
},
{
  text: '昨天',
  value() {
    const date = new Date();
    date.setTime(date.getTime() - 3600 * 1000 * 24);
    return date;
  },
}];

@Component
export default class BasicPanelType extends Vue {
  value = new Date();
  options = {
    shortcuts,
    disabledDate(date: Date) {
      return date && date.getTime() > Date.now();
    },
  };
}
</script>

<style lang="scss" scoped></style>
