<template>
  <div>
    <mtd-row>
      <mtd-col :span="12">
        <p>无偏移</p>
        <mpa-date-picker type="date"
          v-model="value"
          :options="options"
          placeholder="选择时间"/>
      </mtd-col>
      <mtd-col :span="12">
        <p>日偏移</p>
        <mpa-date-picker type="date"
          v-model="value1"
          :options="options1"
          placeholder="选择时间" />
      </mtd-col>
    </mtd-row>
  </div>
</template>
<script lang="ts">
import { Vue, Component } from 'vue-property-decorator';

const shortcuts = [
{
  text: '今天',
  value() {
    return new Date();
  },
},
{
  text: '昨天',
  value() {
    const date = new Date();
    date.setTime(date.getTime() - 3600 * 1000 * 24);
    return date;
  },
},
{
  text: '7天前',
  value() {
    const date = new Date();
    date.setTime(date.getTime() - 3600 * 1000 * 24 * 7);
    return date;
  },
}];

const shortcuts1 = [
{
  text: '今天',
  value() {
    return 0;
  },
},
{
  text: '昨天',
  value() {
    return -1;
  },
},
{
  text: '7天前',
  value() {
    return -7;
  },
}];

@Component
export default class Offsets extends Vue {
  value = new Date();
  value1 = -3;
  options = {
    shortcuts
  };
  options1 = {
    shortcuts: shortcuts1,
    offsetType: true,
    disabledDate(date: Date) {
      return date && date.getTime() > Date.now();
    },
  };
}
</script>

<style lang="scss" scoped></style>
