<template>
  <div>
    <mpa-date-picker
      type="daterange"
      v-model="value"
      placeholder="选择时间"
      :options="rangeOptions" />
  </div>
</template>
<script lang="ts">
import { Vue, Component } from 'vue-property-decorator';

const rangeShortcuts = [
{
  text: '快捷筛选',
  shortcutsPanel: [
    {
      text: '最近7天',
      value() {
        const date = new Date();
        date.setTime(date.getTime() - 3600 * 1000 * 24 * 7);
        return [date, new Date()];
      },
    }, {
      text: '最近30天',
      value() {
        const date = new Date();
        date.setTime(date.getTime() - 3600 * 1000 * 24 * 30);
        return [date, new Date()];
      },
    }
  ],
},
{
  text: '最近7天',
  value() {
    const date = new Date();
    date.setTime(date.getTime() - 3600 * 1000 * 24 * 7);
    return [date, new Date()];
  },
}, {
  text: '最近30天',
  value() {
    const date = new Date();
    date.setTime(date.getTime() - 3600 * 1000 * 24 * 30);
    return [date, new Date()];
  },
}];

@Component
export default class QuickChoose extends Vue {
  value = [];
  rangeOptions = {
    shortcuts: rangeShortcuts,
    disabledDate(date: Date) {
      return date && date.getTime() > Date.now();
    },
  };
}
</script>

<style lang="scss" scoped></style>
