import Input from './mpa-multiple-input.vue';
import { withFormItem } from '@components/form-item';

const MpaMultipleInput = withFormItem('MpaMultipleInput', Input, {
    withRef: true,
    methods: {
        focus() {
            return this.$refs.wrappedInstance.focus();
        },
        blur() {
            return this.$refs.wrappedInstance.blur();
        },
        select() {
            return this.$refs.wrappedInstance.select();
        },
    },
});

/* istanbul ignore next */
MpaMultipleInput.install = function (Vue) {
    Vue.component(MpaMultipleInput.name, MpaMultipleInput);
};

export default MpaMultipleInput;

