// todo 目前直接引用 date-picker 组件的文件，导致单独 datepicker、timepicker 包中都会
// 包含这个文件
import TimePicker from '@components/mpa-date-picker/picker/time-picker.js';
import CreateComponent from '@components/mpa-date-picker/control';
import { withFormItem } from '@components/form-item';

const MpaTimePicker = withFormItem(
  'MpaTimePicker',
  CreateComponent('MpaTimePicker', TimePicker),
  {
    withRef: true,
  },
);

/* istanbul ignore next */
MpaTimePicker.install = function (Vue) {
  Vue.component(MpaTimePicker.name, MpaTimePicker);
};

export default MpaTimePicker;
